import React from 'react'
// import { withRouter } from 'react-router-dom'
import NetlifyForm from 'react-netlify-form'

import { SecondaryButton } from '../styled/buttons'


const GetInTouch = () => {
  return (
    <NetlifyForm name="contact">
      {({ loading, error, success }) => (
        <div>
          {loading && <div>Loading...</div>
          }
          {error && <div>Your information was not sent. Please try again later.</div>
          }
          {success && <div>Thanks for getting in touch!</div>
          }
          {!loading && !success && (
            <>
              <div>
                <label
                  className="forms__label"
                  htmlFor="name"
                >
                  Your Name:
                </label>
                <input
                  className="forms__input"
                  id="name"
                  type="text"
                  name="name"
                />
              </div>

              <div>
                <label
                  className="forms__label"
                  htmlFor="email"
                >
                  Your Email:
                </label>
                <input
                  className="forms__input"
                  id="email"
                  type="email"
                  name="email"
                />
              </div>

              <div>
                <label
                  className="forms__label"
                  htmlFor="message"
                >
                  Message:
                </label>
                <textarea
                  className="forms__textarea"
                  id="message"
                  name="message"
                />
              </div>
              <SecondaryButton pullRight>Send</SecondaryButton>
            </>
          )}
        </div>
      )}
    </NetlifyForm>
  )
}

export default GetInTouch

